 getField("ARCHIVAR").onchange = function() {
  
  if (getField('ARCHIVAR').value == '1')
  {
    document.getElementById('id_div_subtitulo2').style.display = "none";
    document.getElementById('id_div_destinatarios_grid').style.display = "none";
    document.getElementById('id_div_comentarios').style.display = "block";

  }
  if (getField('ARCHIVAR').value != '1')
  {  
    document.getElementById('id_div_subtitulo2').style.display = "block";
    document.getElementById('id_div_destinatarios_grid').style.display = "block";
    document.getElementById('id_div_comentarios').style.display = "none";
  }  
}
    
function valida_form()
  {    
    
    var iRows = Number_Rows_Grid('DESTINATARIOS', 'DEPARTAMENTO');
    bContinue = true;
    if (getField('ARCHIVAR').value == '2') {
       for (var i = 1; i <= iRows; i++) {
         if ((getGridField('DESTINATARIOS', i, 'DEPARTAMENTO').value == '') || (getGridField('DESTINATARIOS', i, 'USUARIO').value == '') ||  (getGridField('DESTINATARIOS', i, 'FECHA_ALERTA').value == '')) {
          bContinue = false;
        }
      }
   }

   if (!bContinue) {
     alert('Por favor seleccione a destinatarios, acción y fechas válidas');
     return false;
  }
 
  if (getField('ARCHIVAR').value == '1')
  {
  if(!ValidaCampos('COMENTARIOS',"Un comentario es requerido")){ return false;}
  }  
  
  return true;
    
    
  }


/*Valida campos de texto cuando es requerido*/
function ValidaCampos(variable, mensaje){
    var classIN = 'module_app_input___gray';
    var valor_form = new input(getField(variable));
    valor_form.onclick=function(){
        valor_form.className=classIN;
    }
    if (getField(variable).value == ''){
      valor_form.failed();
      valor_form.title=mensaje;
      alert(mensaje);
      var AprOk = false;
    }else{
       valor_form.passed();
       valor_form.title="";
      var AprOk = true;
    }
    return AprOk;
    
};

/*Valida campos dropdwon cuando es requerido*/
function ValidaDropdown(variable, mensaje){  
    var oObjeto = new input(getField(variable));
    
    if (getField(variable).value == 0)
    {    
      oObjeto.failed();
      var retorno = false;
      alert(mensaje);
    }
    else  
    {    
      oObjeto.passed();
      var retorno = true;
    }    
    return retorno;
  };]]></JS1>
